INSERT INTO `categories` VALUES (1,'poyasa-iz-shersti','Пояса из шерсти','Пояса из шерсти','Пояса из шерсти оптом','Изделия из шерсти (пояса) - за счет своего согревающего и колющего воздействия, а также активного  притока крови к больному месту, ускоряют восстановление, осуществляют микромассаж, положительно влияют на организм. ','2016-05-17 18:26:59','2016-05-17 18:27:01',1,1),(2,'tabletnicy','Таблетницы','Таблетницы','Таблетницы оптом','Таблетницы - специально выполненные контейнеры, из безопасного материала, помогают упорядочить и распланировать прием таблеток. Для удобства фасовки и планирования, контейнеры выполнены на разные промежутки  времени и маркированы от 2 приемов лекарственных средств в день до 14 в неделю.','2016-05-26 18:27:54','2016-05-26 18:27:56',7,1),(3,'applikatory','Ипликаторы игольчатые','Аппликаторы игольчатые','фыв','Ипликатор - воздействуя на биологически активные точки организма, помогает снять усталость, напряжение и боль в мышцах. Стимулирует кровообращение, способствует выработке эндорфинов и повышению жизненного тонуса.','2016-05-27 20:25:35','2016-05-27 20:25:37',3,1),(4,'plechevye-poyasa','Плечевые пояса','Плечевые пояса','ф','Плечевые пояса - специальная конструкция пояса, а также расположенные ленты помогают поддерживать положение плеч и позвоночника в правильном положении. Вследствие чего оказывают положительное воздействие на позвоночник и помогают избавиться от болевых ощущений в области спины и шеи.','2016-05-30 10:27:00','2016-05-30 10:27:01',6,1),(5,'bandazhi','Бандажи','Бандажи ','а','Изделия из шерсти (бандажи) - за счет своего согревающего и колющего воздействия, а также активного  притока крови к больному месту, ускоряют восстановление, осуществляют микромассаж, положительно влияют на организм. ','2016-05-30 12:09:54','2016-05-30 12:09:54',4,1),(6,'nakolenniki-i-nalokotniki','Наколенники и налокотники из шерсти ','Наколенники и налокотники из шерсти','а','Изделия из шерсти (Наколенники и налокотники) - за счет своего согревающего и колющего воздействия, а также активного  притока крови к больному месту, ускоряют восстановление, осуществляют микромассаж, положительно влияют на организм. ','2016-05-30 12:24:09','2016-05-30 12:24:10',2,1),(7,'izdeliya-iz-shersti','Изделия из шерсти (без РУ)','Изделия из шерсти',' ',' ','2017-02-17 21:08:11','2017-02-17 21:08:13',8,1),(8,'izdeliya-iz-shersti2','Изделия из шерсти','Изделия из шерсти',' ',' ','2017-10-16 19:42:44','2017-10-16 19:42:46',9,1);
